<?php
function naplozo($naploElemNev, $kapcsolId = 0, $kapcsoltTabla = '', $uzenet = '') {	if(beallitasOlvasas("log_kikapcsolva")=='1') return;	$tag = ws_belepesEllenorzes();	$ci = getCI();						if(!$uzenet) $uzenet = '---';		$naploElemek = globalisMemoria('naploElemek');	if(!$naploElemek) {				$elemek = $ci->Sql->gets(DBP."naploelemek");		$naploElemek = array();		if($elemek) foreach($elemek as $sor) $naploElemek[strToUrl($sor->nev)] = $sor->id;	}		if(isset($naploElemek[strToUrl($naploElemNev)])) {		$a = array('naploelemid' => $naploElemek[strToUrl($naploElemNev)], 'mentetid' => $kapcsolId, 'felhasznaloid'=> (isset($tag->id)?$tag->id:0),'uzenet'=>$uzenet);		$ci->Sql->sqlSave($a, DBP.'naplobejegyzesek');	} else {		$a = array('nev' => $naploElemNev, 'tabla' => $kapcsoltTabla);		$naploElemek[strToUrl($naploElemNev)] = $elemid = $ci->Sql->sqlSave($a, DBP.'naploelemek');				$a = array('naploelemid' => $elemid, 'mentetid' => $kapcsolId,'felhasznaloid'=> (isset($tag->id)?$tag->id:0),'uzenet'=>$uzenet);		$ci->Sql->sqlSave($a,DBP. 'naplobejegyzesek');				globalisMemoria('naploElemek', $naploElemek);	}		return;		} 