es = function() {			// cb			hiba = false;									vnev = $('#lname').val().trim();			knev = $('#fname').val().trim();			email = $('#email').val().trim();			if(vnev.length<2) {				hiba = true;				$('#lname').parent().addClass("error");			} else {				$('#lname').parent().removeClass("error");			}						if(knev.length<3) {				hiba = true;				$('#fname').parent().addClass("error");			} else {				$('#fname').parent().removeClass("error");			}			if(!this.validateEmail(email)) {				hiba = true;				$('#email').parent().addClass("error");			} else {				$('#email').parent().removeClass("error");			}						if($('#pwd1').val().trim()!="") {								pwd1 = $('#pwd1').val().trim();				pwd2 = $('#pwd2').val().trim();				if(pwd1!=pwd2) {					hiba = true;					$('#pwd2').parent().addClass("error");				} else {					$('#pwd2').parent().removeClass("error");				}				if(pwd1.length < 6) {					hiba = true;					$('#pwd1').parent().addClass("error");				} else {					$('#pwd1').parent().removeClass("error");				}							}						if(!hiba) $('#urlap').submit();		}		this.validateEmail = function(email) {			var re = /^(([^<>()\[\]\\.,;:\s@"]+(\.[^<>()\[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/;			return re.test(String(email).toLowerCase());		}	}	vJs = new Validalas;	vJs.init();</script>